import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Helmet } from 'react-helmet'
import { useForm } from 'react-hook-form';
import { Link, useNavigate, useParams } from 'react-router-dom';
import 'react-toastify/dist/ReactToastify.css';
import { ToastContainer, toast } from 'react-toastify'

export const UserDashBoard = () => {
  var style = {
    width: "90px"
  }
  var style1 = {
    visibility: "visible; animation-delay: 0.1s; animation-name: fadeInUp;"
  }
  var style2 = {
    visibility: "visible; animation-delay: 0.5s; animation-name: zoomIn;"
  }

  var style3 = {
    visibility: "visible; animation-delay: 0.1s; animation-name: fadeInUp;"
  }

  var style4 = {
    height: "250px"
  }

  var style5 = {
    height: "250px"
  }

  var style6 = {
    height: "48px"
  }

  var style7 = {
    visibility: "visible; animation-delay: 0.1s; animation-name: fadeIn"
  }
  var style8 = {
    visibility: "visible; animation-delay: 0.3s; animation-name: fadeInUp"
  }

  var navigate = useNavigate()

  const [users, setusers] = useState();
  const { id } = useParams();

  const getApi = () => {

    axios.get("http://localhost:5000/usertask/usertask2/" + id).then((res) => {

      console.log(res.data.module)
      setusers(res.data.module)

    }).catch((err) => {

      console.log(err)
    })


  }
  useEffect(() => {

    getApi()
    // navigate("/admindashboard")


  }, [])



  const updateProject = (id) => {
    axios.put("http://localhost:5000/task/task/" + id).then((res) => {
      console.log(res.data.project[0])
      setusers(res.data.project[0])

    }).catch((err) => {

      console.log(err)
    })


  }
  const deleteProject = (id) => {

    axios.delete("http://localhost:5000/task/task/" + id).then((res) => {


      getApi()



    }).catch((err) => {

      console.log(err)
    })
  }
  return (
    <div class="container-xxl bg-white p-0">
      <ToastContainer
        position="top-left"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
      />
      <Helmet>
        <link href='https://unpkg.com/boxicons@2.0.9/css/boxicons.min.css' rel='stylesheet' />
        <link rel="stylesheet" href="https://unicons.iconscout.com/release/v4.0.0/css/line.css" />
        <link rel="stylesheet" href="../assets/vendor/fonts/jaymin.css" />
      </Helmet>

      <div class="container-xxl py-5 bg-primary hero-header mb-5">
        <div class="container my-5 py-5 px-lg-5">
          <div class="row g-5 py-5">
            <div class="col-12 text-center">
              <h1 class="text-white animated zoomIn">UserDashBoard page</h1>
              <hr class="bg-white mx-auto mt-0" style={style} />
              <nav aria-label="breadcrumb">
                <ol class="breadcrumb justify-content-center">
                  <li class="breadcrumb-item"><a class="text-white" href="#">Home</a></li>
                  <li class="breadcrumb-item"><a class="text-white" href="#">Pages</a></li>
                  <li class="breadcrumb-item text-white active" aria-current="page">UserDashBoard Page</li>
                </ol>
              </nav>
            </div>
          </div>
        </div>
      </div>









      <div class="data">
        <div class="content-data">
          <div class="head">
            <h3>UserTask List</h3>
            <form action="#">
              <div class="form-group">
                <input type="text" placeholder="Search..." />

              </div>
            </form>
            <div class="refresh">


            </div>
            <button class="view-team-btn" onClick={() => { navigate("/user") }}>Add Task</button>

          </div>

          <table class="ann" >
            <tr>
              <th>Id</th>
              <th>Task</th>
              <th>User</th>

              

            </tr>
            {
              users?.map((u) => {
                return (<tr>
                  <td>{u._id}</td>
                  <td>  {[u?.task?.map((d) => d.title).join(", ")]}</td> 
                  <td>
                  {[u?.user?.map((d) => d.name).join(", ")]}</td> 



                  <td>
                    <button class="button" onClick={() => { navigate(`/updatetask/${u._id}`) }}><i class="uil uil-edit"></i></button><br />
                    <button class="button" onClick={() => { deleteProject(u._id) }}><i class="uil uil-trash-alt"></i></button><br />
                    <button class="button" onClick={() => { navigate(`/detailpage/${u._id}`) }}><i class='bx bx-info-circle'></i></button>




                  </td>
                </tr>)
              })
            }

          </table>
        </div>
      </div>
      {/* </section> */}


      <div class="container-fluid bg-primary text-light footer mt-5 pt-5 wow fadeIn" data-wow-delay="0.1s" style={style7}>
        <div class="container py-5 px-lg-5">
          <div class="row g-5">
            <div class="col-md-6 col-lg-3">
              <h5 class="text-white mb-4">Get In Touch</h5>
              <p><i class="fa fa-map-marker-alt me-3"></i>E-34 partik tenament</p>
              <p><i class="fa fa-phone-alt me-3"></i>7284961583</p>
              <p><i class="fa fa-envelope me-3"></i>jaymin@gmail.com</p>
              <div class="d-flex pt-2">
                <a class="btn btn-outline-light btn-social" href=""><i class="fab fa-twitter"></i></a>
                <a class="btn btn-outline-light btn-social" href=""><i class="fab fa-facebook-f"></i></a>
                <a class="btn btn-outline-light btn-social" href=""><i class="fab fa-youtube"></i></a>
                <a class="btn btn-outline-light btn-social" href=""><i class="fab fa-instagram"></i></a>
                <a class="btn btn-outline-light btn-social" href=""><i class="fab fa-linkedin-in"></i></a>
              </div>
            </div>
            <div class="col-md-6 col-lg-3">
              <h5 class="text-white mb-4">Popular Link</h5>
              <a class="btn btn-link" href="">About Us</a>
              <a class="btn btn-link" href="">Contact Us</a>
              <a class="btn btn-link" href="">Privacy Policy</a>
              <a class="btn btn-link" href="">Terms &amp; Condition</a>
              <a class="btn btn-link" href="">Career</a>
            </div>

            <div class="col-md-6 col-lg-6">
              <h5 class="text-white mb-4">Newsletter</h5>
              <p>Lorem ipsum dolor sit amet elit. Phasellus nec pretium mi. Curabitur facilisis ornare velit non vulpu</p>
              <div class="position-relative w-100 mt-3">
                <input class="form-control border-0 rounded-pill w-100 ps-4 pe-5" type="text" placeholder="Your Email" style={style6} />
                <button type="button" class="btn shadow-none position-absolute top-0 end-0 mt-1 me-2"><i class="fa fa-paper-plane text-primary fs-4"></i></button>
              </div>
            </div>
          </div>
        </div>
        <div class="container px-lg-5">
          <div class="copyright">
            <div class="row">
              <div class="col-md-6 text-center text-md-start mb-3 mb-md-0">
                Â© <a class="border-bottom" href="#">Bug tracking</a>, All Right Reserved.

                <a class="border-bottom" href="https://htmlcodex.com">HTML Codex</a>
              </div>
              <div class="col-md-6 text-center text-md-end">
                <div class="footer-menu">
                  <a href="">Home</a>
                  <a href="">Cookies</a>
                  <a href="">Help</a>
                  <a href="">FQAs</a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>



    </div>



  )
}
